-- Branch Table
CREATE TABLE branch (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    brand_id UUID NOT NULL REFERENCES brand(id) ON DELETE CASCADE ON UPDATE CASCADE,
    address TEXT NOT NULL,
    phone VARCHAR(10) NOT NULL,
    status VARCHAR(10) NOT NULL,
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- Member Information
CREATE TABLE member_information (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    full_name VARCHAR(255) NOT NULL,
    username VARCHAR(255) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL,
    role VARCHAR(50) NOT NULL,
    email VARCHAR(255) UNIQUE NOT NULL,
    phone VARCHAR(10) UNIQUE NOT NULL,
    gender VARCHAR(6) NOT NULL,
    address VARCHAR(255) NOT NULL,
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Customer Table
CREATE TABLE customer (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    member_information_id UUID NOT NULL REFERENCES member_information(id) ON DELETE CASCADE ON UPDATE CASCADE,
    brand_id UUID NOT NULL REFERENCES brand(id) ON DELETE CASCADE ON UPDATE CASCADE,
    status VARCHAR(50) NOT NULL,
    avatar TEXT,
    cart JSONB DEFAULT '{"total_price": 0, "items": []}'
);



-- Staff Table
CREATE TABLE staff (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    member_information_id UUID NOT NULL REFERENCES member_information(id) ON DELETE CASCADE ON UPDATE CASCADE,
    branch_id UUID NOT NULL REFERENCES branch(id) ON DELETE CASCADE ON UPDATE CASCADE,
    position VARCHAR(50),
    avatar TEXT,
    salary INTEGER NOT NULL
);



-- User's Order Table
CREATE TABLE "order" (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    customer_id UUID NOT NULL REFERENCES customer(id) ON DELETE CASCADE ON UPDATE CASCADE,
    branch_id UUID NOT NULL REFERENCES branch(id) ON DELETE CASCADE ON UPDATE CASCADE,
    status VARCHAR(50) NOT NULL,
    type VARCHAR(50),
    preorder_time TIME,
    late BOOLEAN NOT NULL DEFAULT false,
    resolved BOOLEAN NOT NULL DEFAULT false,
    payment_method VARCHAR(50) NOT NULL,
    cart JSONB DEFAULT '{"total_price": 0, "items": []}',
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- Feedback Table
CREATE TABLE feedback (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    customer_id UUID NOT NULL REFERENCES customer(id) ON DELETE CASCADE ON UPDATE CASCADE,
    branch_id UUID NOT NULL REFERENCES branch(id) ON DELETE CASCADE ON UPDATE CASCADE,
    type VARCHAR(255) NOT NULL,
    content TEXT NOT NULL,
    status VARCHAR(10) NOT NULL DEFAULT 'Pending', 
    solved_by UUID REFERENCES member_information(id) ON DELETE CASCADE ON UPDATE CASCADE,
    updated_at TIMESTAMP,
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Feedbacks

-- Product Table
CREATE TABLE product (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    brand_id UUID NOT NULL REFERENCES brand(id) ON DELETE CASCADE ON UPDATE CASCADE,
    name VARCHAR(255) NOT NULL,
    type VARCHAR(255) NOT NULL,
    description TEXT NOT NULL,
    price INTEGER NOT NULL,
    photo TEXT NOT NULL,
    stock INTEGER NOT NULL,
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- Blog Table
CREATE TABLE blog (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    staff_id UUID NOT NULL REFERENCES staff(id) ON DELETE CASCADE ON UPDATE CASCADE,
    content TEXT NOT NULL,
    title TEXT NOT NULL,
    photo JSONB DEFAULT '{}',
    status VARCHAR(50),
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- Reservation Table
CREATE TABLE reservation (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    full_name VARCHAR(255) NOT NULL,
    phone VARCHAR(255) NOT NULL,
    branch_id UUID NOT NULL REFERENCES branch(id) ON DELETE CASCADE ON UPDATE CASCADE,
    reservation_time TIME NOT NULL,
    reservation_date DATE NOT NULL,
    number_of_customer INTEGER NOT NULL,
    status VARCHAR(50) NOT NULL,
    place VARCHAR(50),
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- Voucher
CREATE TABLE voucher (
    id UUID NOT NULL PRIMARY KEY DEFAULT gen_random_uuid(),
    title VARCHAR(255) NOT NULL,
    discount_percent INTEGER NOT NULL,
    code TEXT NOT NULL,
    status VARCHAR(50) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL,
    type VARCHAR(50) NOT NULL,
    discount_type VARCHAR(50) NOT NULL,
    description TEXT NOT NULL,
    brand_id UUID NOT NULL REFERENCES brand(id) ON DELETE CASCADE ON UPDATE CASCADE,
    date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- Session Table
CREATE TABLE session (
  sid varchar PRIMARY KEY,
  sess json NOT NULL,
  expire timestamp NOT NULL
);

