INSERT INTO feedback (id, customer_id, branch_id, type, content, status, solved_by, updated_at) VALUES
    (gen_random_uuid(), 'a1b2c3d4-e5f6-4a5b-9c8d-1e2f3a4b5c6d', '109db7f7-52a2-41fd-bfa6-9637df5cc248', 'Complaint', 'The red velvet cake I ordered was dry and the frosting had an artificial taste. I was really disappointed as this was for my daughter''s birthday.', 'Resolved', 'a1c3e5a7-b2d4-f6a8-a0a2-a4a6a8a0a2a4', '2025-05-01 14:23:45'),
    (gen_random_uuid(), 'b2c3d4e5-f6a7-5b6c-0d1e-2f3a4b5c6d7e', 'ff7b7703-bd86-4a78-8e87-adb745c628f9', 'Suggestion', 'Have you considered adding more vegan options to your menu? I love your coffee but can''t enjoy any of your pastries as a vegan customer.', 'Pending', NULL, NULL),
    (gen_random_uuid(), 'c3d4e5f6-a7b8-6c7d-1e2f-3a4b5c6d7e8f', 'bd574b57-9ccd-4e9a-b7c5-2e35afbc513c', 'Complaint', 'I waited over 45 minutes for my order when the shop wasn''t even busy. The staff seemed unorganized and didn''t apologize for the wait.', 'Resolved', 'c3e5a7c9-d0e2-f4a6-b8c0-d2e4f6a8b0c2', '2025-05-03 09:15:22'),
    (gen_random_uuid(), 'd4e5f6a7-b8c9-7d8e-2f3a-4b5c6d7e8f9a', 'a06edc09-9be1-4e68-b399-b2453c35edfe', 'Suggestion', 'Your coffee is amazing! I wish you would offer a coffee subscription service so I could have it delivered to my home regularly.', 'Pending', NULL, NULL),
    (gen_random_uuid(), 'e5f6a7b8-c9d0-8e9f-3a4b-5c6d7e8f9a0b', '109db7f7-52a2-41fd-bfa6-9637df5cc248', 'Complaint', 'Found a hair in my chocolate mousse cake. This is unacceptable for the premium prices you charge.', 'Resolved', 'f6b8d0f6-a8b0-c2d4-e6f8-a0b2c4d6e8f0', '2025-04-30 16:45:12'),
    (gen_random_uuid(), 'f6a7b8c9-d0e1-9f0a-4b5c-6d7e8f9a0b1c', 'ff7b7703-bd86-4a78-8e87-adb745c628f9', 'Suggestion', 'Your pastries are delicious but the portions are too large for one person. Have you thought about offering mini versions?', 'Pending', NULL, NULL),
