    (gen_random_uuid(), 'd0e1f2a3-b4c5-3d4e-8f9a-0b1c2d3e4f5a', 'ff7b7703-bd86-4a78-8e87-adb745c628f9', 'Suggestion', 'I think you should consider offering afternoon tea sets with finger sandwiches and assorted pastries.', 'Pending', NULL, NULL),
