    (gen_random_uuid(), 'd6e7f8a9-b0c1-9d0e-4f5a-6b7c8d9e0f1a', 'a06edc09-9be1-4e68-b399-b2453c35edfe', 'Suggestion', 'Your cafe would benefit from a loyalty program. I come here 3 times a week and would appreciate some perks for my regular business.', 'Pending', NULL, NULL),
